        this.recipeItem = recipeItem;
    }

    @Override
    protected void build() {
        clear();

        // Navigation buttons
        addBackButton();
        addHomeButton();

        // Check if this is an Automated Panning Machine - show special recipe view
        if (recipeItem.getId().equals("automated_panning_machine")) {
            buildAutomatedPanningRecipes();
            return;
        }

