package org.ThefryGuy.techFactory.workstations.multiblocks;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.block.Block;
import org.bukkit.block.Dispenser;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.ThefryGuy.techFactory.recipes.RecipeItem;
import org.ThefryGuy.techFactory.recipes.resources.*;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Basic Workbench Machine - Handles the actual functionality
 * 
 * Structure (like Slimefun's Enhanced Crafting Table):
 * - Bottom: Dispenser (holds the recipe items)
 * - Top: Crafting Table (player right-clicks this)
 * 
 * How it works:
 * 1. Player places Dispenser on ground
 * 2. Player places Crafting Table on top of Dispenser
 * 3. Player puts recipe items in Dispenser
 * 4. Player right-clicks Crafting Table
 * 5. If recipe matches, output item is given to player
 * 
 * The recipe data is in recipes/workstations/multiblocks/BasicWorkbench.java
 */
public class BasicWorkbenchMachine {

    // Workbench recipes: input item IDs -> output RecipeItem
    private static final Map<String, RecipeItem> WORKBENCH_RECIPES = new HashMap<>();

    static {
        // Initialize workbench recipes
        // Carbon Chunk: 8x Compressed Carbon + 1x Flint
        addRecipe(new CarbonChunk(), "compressed_carbon:8", "flint:1");

        // Gold Pan: 5x Stone + 1x Bowl
        addRecipe(new org.ThefryGuy.techFactory.recipes.tools.GoldPan(), "stone:5", "bowl:1");
    }

    /**
     * Helper method to add a recipe with sorted item IDs
     */
    private static void addRecipe(RecipeItem output, String... items) {
        List<String> itemList = new java.util.ArrayList<>();
        for (String item : items) {
            // Add ":1" if no quantity specified
            if (!item.contains(":")) {
                itemList.add(item + ":1");
            } else {
                itemList.add(item);
            }
        }
        java.util.Collections.sort(itemList);
        String recipeKey = String.join(",", itemList);
        WORKBENCH_RECIPES.put(recipeKey, output);
    }

    /**
     * Check if a valid Basic Workbench multiblock exists at this location.
     * 
     * Structure:
     * [Crafting Table] ← Top (player clicks here)
     * [Dispenser]      ← Bottom (holds items)
     * 
     * @param craftingTable The crafting table block (top)
     * @return true if valid multiblock structure
     */
    public static boolean isValidStructure(Block craftingTable) {
        // Check if top is a crafting table
        if (craftingTable.getType() != Material.CRAFTING_TABLE) {
            return false;
        }

        // Check if block below is a dispenser
        Block below = craftingTable.getRelative(0, -1, 0);
        return below.getType() == Material.DISPENSER;
    }

    /**
     * Get the dispenser below the crafting table.
     * 
     * @param craftingTable The crafting table block
     * @return The dispenser block, or null if not valid structure
     */
    public static Dispenser getDispenser(Block craftingTable) {
        if (!isValidStructure(craftingTable)) {
            return null;
        }

        Block below = craftingTable.getRelative(0, -1, 0);
        return (Dispenser) below.getState();
    }

    /**
     * Open the Basic Workbench GUI for the player.
     * This shows the dispenser inventory where they can place recipe items.
     * 
     * @param player The player who clicked
     * @param craftingTable The crafting table block
     */
    public static void openWorkbench(Player player, Block craftingTable) {
        Dispenser dispenser = getDispenser(craftingTable);
        if (dispenser == null) {
            player.sendMessage(ChatColor.RED + "Invalid multiblock structure!");
            return;
        }

        // Open the dispenser inventory
        player.openInventory(dispenser.getInventory());
        player.sendMessage(ChatColor.GREEN + "Basic Workbench opened!");
        player.sendMessage(ChatColor.GRAY + "Place items in the 3x3 grid and close to craft.");
    }

    /**
     * Process a recipe when the player closes the dispenser.
     * Output goes into the dispenser or an adjacent chest.
     *
     * @param dispenser The dispenser inventory
     * @param player The player who closed it
     */
    public static void processRecipe(Dispenser dispenser, Player player) {
        Inventory inv = dispenser.getInventory();

        // Build a map of item IDs and their quantities in the inventory
        Map<String, Integer> itemCounts = new HashMap<>();

        for (int i = 0; i < inv.getSize(); i++) {
            ItemStack item = inv.getItem(i);
            if (item == null) continue;

            String itemId = getItemId(item);
            if (itemId == null) {
                // Check for vanilla items
                itemId = getVanillaItemId(item);
            }

            if (itemId != null) {
                itemCounts.put(itemId, itemCounts.getOrDefault(itemId, 0) + item.getAmount());
            }
        }

        // Build recipe key from items in inventory
        List<String> itemList = new java.util.ArrayList<>();
        for (Map.Entry<String, Integer> entry : itemCounts.entrySet()) {
            itemList.add(entry.getKey() + ":" + entry.getValue());
        }
        java.util.Collections.sort(itemList);
        String recipeKey = String.join(",", itemList);

        // Check if this matches a recipe
        RecipeItem output = WORKBENCH_RECIPES.get(recipeKey);
        if (output != null) {
            // Recipe matched! Clear all items and output the result
            inv.clear();

            ItemStack outputItem = output.getItemStack();

            // Try to output to adjacent chest first, then dispenser
            if (!outputToChest(dispenser.getBlock(), outputItem)) {
                inv.addItem(outputItem);
            }

            player.sendMessage(ChatColor.GREEN + "Crafted " + outputItem.getItemMeta().getDisplayName() + "!");
        }
    }

    /**
     * Get the item ID from an ItemStack's NBT data.
     *
     * @param item The item to check
     * @return The item ID, or null if not a TechFactory item
     */
    private static String getItemId(ItemStack item) {
        if (!item.hasItemMeta()) {
            return null;
        }

        var meta = item.getItemMeta();
        var pdc = meta.getPersistentDataContainer();
        var key = new org.bukkit.NamespacedKey("techfactory", "item_id");

        if (pdc.has(key, org.bukkit.persistence.PersistentDataType.STRING)) {
            return pdc.get(key, org.bukkit.persistence.PersistentDataType.STRING);
        }

        return null;
    }

    /**
     * Get vanilla item ID for recipe matching
     */
    private static String getVanillaItemId(ItemStack item) {
        Material mat = item.getType();
        switch (mat) {
            case GLASS: return "glass";
            case GLASS_PANE: return "glass_pane";
            case LAPIS_LAZULI: return "lapis_lazuli";
            case FLINT: return "flint";
            default: return null;
        }
    }

    /**
     * Try to output items to an adjacent chest.
     * Checks all blocks adjacent to the entire multiblock structure.
     *
     * @param dispenserBlock The dispenser block
     * @param output The item to output
     * @return true if output was successful, false if no chest found
     */
    private static boolean outputToChest(Block dispenserBlock, ItemStack output) {
        // Get the crafting table (above dispenser)
        Block craftingTable = dispenserBlock.getRelative(0, 1, 0);

        // Check blocks adjacent to BOTH the dispenser AND the crafting table
        Block[] blocksToCheck = {dispenserBlock, craftingTable};

        for (Block centerBlock : blocksToCheck) {
            // Check all 6 adjacent blocks around this center block
            Block[] adjacent = {
                centerBlock.getRelative(1, 0, 0),   // East
                centerBlock.getRelative(-1, 0, 0),  // West
                centerBlock.getRelative(0, 0, 1),   // South
                centerBlock.getRelative(0, 0, -1),  // North
                centerBlock.getRelative(0, 1, 0),   // Up
                centerBlock.getRelative(0, -1, 0)   // Down
            };

            for (Block block : adjacent) {
                // Skip if this block is part of the multiblock itself
                if (block.equals(dispenserBlock) || block.equals(craftingTable)) {
                    continue;
                }

                if (block.getType() == Material.CHEST || block.getType() == Material.TRAPPED_CHEST) {
                    if (block.getState() instanceof org.bukkit.block.Chest) {
                        org.bukkit.block.Chest chest = (org.bukkit.block.Chest) block.getState();
                        Inventory chestInv = chest.getInventory();

                        // Try to add item to chest
                        chestInv.addItem(output);
                        return true;
                    }
                }
            }
        }

        return false; // No chest found
    }

    /**
     * Break the multiblock and drop items.
     * Called when any part of the multiblock is broken.
     * 
     * @param location The location where it was broken
     * @param player The player who broke it (can be null)
     */
    public static void breakMultiblock(Location location, Player player) {
        // TODO: Drop the Basic Workbench controller item
        // For now, just send a message
        if (player != null) {
            player.sendMessage(ChatColor.RED + "Basic Workbench destroyed!");
        }
    }
}

