package org.ThefryGuy.techFactory.gui;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.ThefryGuy.techFactory.gui.menubuttons.BackButton;

import java.util.List;

public class DustsMenu {

    public static void openDustsMenu(Player player) {
        // Create an 18-slot chest inventory (2 rows for dusts)
        Inventory gui = Bukkit.createInventory(null, 18, ChatColor.LIGHT_PURPLE + "Dusts Menu");

        // Back button in slot 0
        gui.setItem(0, BackButton.getItem());

        // Define dust items
        gui.setItem(1, createDustItem("Iron Dust", Material.IRON_INGOT, ChatColor.GRAY + "Metal dust used in advanced crafting."));
        gui.setItem(2, createDustItem("Gold Dust", Material.GOLD_INGOT, ChatColor.GOLD + "Rare and valuable dust for premium items."));
        gui.setItem(3, createDustItem("Copper Dust", Material.COPPER_INGOT, ChatColor.GOLD + "Common metal dust used in many recipes."));
        gui.setItem(4, createDustItem("Tin Dust", Material.AMETHYST_SHARD, ChatColor.GRAY + "Tin dust for alloy crafting."));
        gui.setItem(5, createDustItem("Silver Dust", Material.DIAMOND, ChatColor.WHITE + "Precious metal dust for high-end items."));
        gui.setItem(6, createDustItem("Aluminum Dust", Material.QUARTZ, ChatColor.WHITE + "Light metal dust for aerospace materials."));
        gui.setItem(7, createDustItem("Lead Dust", Material.DARK_OAK_BUTTON, ChatColor.DARK_GRAY + "Heavy metal dust for shielding."));
        gui.setItem(8, createDustItem("Zinc Dust", Material.LAPIS_LAZULI, ChatColor.BLUE + "Metal dust for corrosion resistance."));
        gui.setItem(9, createDustItem("Magnesium Dust", Material.MAGMA_BLOCK, ChatColor.RED + "Highly reactive metal dust."));

        // Open the inventory for the player
        player.openInventory(gui);
    }

    /**
     * Helper method to create a dust item with display name and lore.
     */
    private static ItemStack createDustItem(String name, Material material, String color) {
        ItemStack item = new ItemStack(material);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(color + name);
            meta.setLore(List.of(ChatColor.GRAY + "A refined powder used in crafting."));
            item.setItemMeta(meta);
        }

        return item;
    }
}

