package org.ThefryGuy.techFactory.listeners;

import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.inventory.ItemStack;
import org.ThefryGuy.techFactory.gui.DustsMenu;
import org.ThefryGuy.techFactory.gui.GuideMenu;
import org.ThefryGuy.techFactory.gui.ResourcesMenu;

public class ResourcesMenuListener implements Listener {

    @EventHandler
    public void onResourcesMenuClick(InventoryClickEvent event) {
        if (!(event.getWhoClicked() instanceof Player player)) return;

        // Check if the inventory is our ResourcesMenu
        if (event.getView().getTitle().equals(ChatColor.AQUA + "Resources Menu")) {
            event.setCancelled(true); // Prevent taking items

            ItemStack clicked = event.getCurrentItem();
            if (clicked == null || !clicked.hasItemMeta()) return;

            // Handle different resource items
            Material type = clicked.getType();

            if (type == Material.OAK_LOG) {
                player.sendMessage(ChatColor.YELLOW + "You clicked the Wood resource!");
            } else if (type == Material.BLAST_FURNACE) {
                // Open the Dusts submenu
                DustsMenu.openDustsMenu(player);
            } else if (type == Material.COBBLESTONE) {
                player.sendMessage(ChatColor.YELLOW + "You clicked the Stone resource!");
            } else if (type == Material.ARROW) {
                // Back button - go back to main menu
                GuideMenu.openGuide(player);
            }
        }
    }
}

