package org.ThefryGuy.techFactory.recipes;

import org.bukkit.Material;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.ChatColor;

import java.util.List;

/**
 * Abstract base class for all dust types in TechFactory.
 */
public abstract class DustType {

    /**
     * Get the unique name/ID of this dust type
     */
    public abstract String getId();

    /**
     * Get the display name of this dust
     */
    public abstract String getDisplayName();

    /**
     * Get the color of this dust for chat messages
     */
    public abstract ChatColor getColor();

    /**
     * Get the material representation for this dust in GUIs
     */
    public abstract Material getMaterial();

    /**
     * Get the lore/description for this dust
     */
    public abstract List<String> getLore();

    /**
     * Get the actual ItemStack representation of this dust for the inventory
     */
    public ItemStack getItemStack() {
        ItemStack item = new ItemStack(getMaterial());
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(getColor() + getDisplayName());
            meta.setLore(getLore());
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Get the recipe for this dust (placeholder for now)
     * TODO: Implement actual recipe logic
     */
    public String getRecipe() {
        return "Recipe not yet implemented for " + getDisplayName();
    }
}
