package org.ThefryGuy.techFactory.recipes.dusts;

import org.bukkit.Material;
import org.bukkit.ChatColor;

import java.util.List;

public class CopperDust extends DustType {

    @Override
    public String getId() {
        return "copper_dust";
    }

    @Override
    public String getDisplayName() {
        return "Copper Dust";
    }

    @Override
    public ChatColor getColor() {
        return ChatColor.of(new java.awt.Color(0xB87333)); // Copper color
    }

    @Override
    public Material getMaterial() {
        return Material.ORANGE_CONCRETE_POWDER;
    }

    @Override
    public List<String> getLore() {
        return List.of(
                ChatColor.GRAY + "Reddish metal powder",
                ChatColor.GRAY + "Used in electrical components"
        );
    }

    @Override
    public String getRecipe() {
        return "Recipe: Copper Ore + Grinding Stone";
    }
}

