+package org.ThefryGuy.techFactory.gui;
+
+import org.ThefryGuy.techFactory.recipes.dusts.*;
+import org.bukkit.Bukkit;
+import org.bukkit.ChatColor;
+import org.bukkit.Material;
+import org.bukkit.entity.Player;
+import org.bukkit.inventory.Inventory;
+import org.bukkit.inventory.ItemStack;
+import org.bukkit.inventory.meta.ItemMeta;
+import org.ThefryGuy.techFactory.gui.menubuttons.BackButton;
+
+public class RecipeMenu {
+
+    /**
+     * Maps ore materials to dust types
+     */
+    private static Material getOreMaterial(String dustId) {
+        return switch (dustId) {
+            case "iron_dust" -> Material.IRON_ORE;
+            case "gold_dust" -> Material.GOLD_ORE;
+            case "copper_dust" -> Material.COPPER_ORE;
+            case "tin_dust" -> Material.STONE; // Placeholder - adjust as needed
+            case "silver_dust" -> Material.STONE; // Placeholder - adjust as needed
+            case "aluminum_dust" -> Material.STONE; // Placeholder - adjust as needed
+            case "lead_dust" -> Material.STONE; // Placeholder - adjust as needed
+            case "zinc_dust" -> Material.STONE; // Placeholder - adjust as needed
+            case "magnesium_dust" -> Material.STONE; // Placeholder - adjust as needed
+            default -> Material.STONE;
+        };
+    }
+
+    public static void openRecipeMenu(Player player, DustType dust) {
+        // Create a 9-slot chest inventory (1 row)
+        // Layout: [Back] [Input Ore] [Grinder] [Output Dust] [Filler] [Filler] [Filler] [Filler] [Filler]
+        Inventory gui = Bukkit.createInventory(null, 9, ChatColor.LIGHT_PURPLE + dust.getDisplayName() + " Recipe");
+
+        // Slot 0: Back button
+        gui.setItem(0, BackButton.getItem());
+
+        // Slot 1: Input material (ore to grind)
+        gui.setItem(1, createInputItem(dust));
+
+        // Slot 2: Grinding stone (machine)
+        gui.setItem(2, createGrinder());
+
+        // Slot 3: Arrow (decoration)
+        gui.setItem(3, createArrow());
+
+        // Slot 4: Output dust
+        gui.setItem(4, dust.getItemStack());
+
+        // Open the inventory for the player
+        player.openInventory(gui);
+    }
+
+    /**
+     * Creates the input ore item
+     */
+    private static ItemStack createInputItem(DustType dust) {
+        Material oreMaterial = getOreMaterial(dust.getId());
+        ItemStack item = new ItemStack(oreMaterial);
+        ItemMeta meta = item.getItemMeta();
+
+        if (meta != null) {
+            meta.setDisplayName(ChatColor.AQUA + "Input Ore");
+            meta.setLore(java.util.List.of(
+                    ChatColor.GRAY + "Place ore here to grind",
+                    ChatColor.GRAY + "into " + dust.getDisplayName()
+            ));
+            item.setItemMeta(meta);
+        }
+
+        return item;
+    }
+
+    /**
+     * Creates the grinding stone (machine) item
+     */
+    private static ItemStack createGrinder() {
+        ItemStack item = new ItemStack(Material.DARK_OAK_WOOD);
+        ItemMeta meta = item.getItemMeta();
+
+        if (meta != null) {
+            meta.setDisplayName(ChatColor.DARK_GRAY + "Grinding Stone");
+            meta.setLore(java.util.List.of(
+                    ChatColor.GRAY + "Machine that grinds ore",
+                    ChatColor.GRAY + "into dust"
+            ));
+            item.setItemMeta(meta);
+        }
+
+        return item;
+    }
+
+    /**
+     * Creates an arrow decoration item
+     */
+    private static ItemStack createArrow() {
+        ItemStack item = new ItemStack(Material.ARROW);
+        ItemMeta meta = item.getItemMeta();
+
+        if (meta != null) {
+            meta.setDisplayName(ChatColor.YELLOW + "â†’");
+            item.setItemMeta(meta);
+        }
+
+        return item;
+    }
+}

