+package org.ThefryGuy.techFactory.listeners;
+
+import org.ThefryGuy.techFactory.gui.DustsMenu;
+import org.bukkit.ChatColor;
+import org.bukkit.Material;
+import org.bukkit.entity.Player;
+import org.bukkit.event.EventHandler;
+import org.bukkit.event.Listener;
+import org.bukkit.event.inventory.InventoryClickEvent;
+import org.bukkit.inventory.ItemStack;
+
+public class RecipeMenuListener implements Listener {
+
+    @EventHandler
+    public void onRecipeMenuClick(InventoryClickEvent event) {
+        if (!(event.getWhoClicked() instanceof Player player)) return;
+
+        // Check if the inventory is a recipe menu (contains "Recipe" in title)
+        String title = event.getView().getTitle();
+        if (title.contains(ChatColor.LIGHT_PURPLE + "") && title.contains("Recipe")) {
+            event.setCancelled(true); // Prevent taking items
+
+            ItemStack clicked = event.getCurrentItem();
+            if (clicked == null || !clicked.hasItemMeta()) return;
+
+            Material type = clicked.getType();
+
+            // Handle back button
+            if (type == Material.ARROW) {
+                // Check if it's actually the back button (first item)
+                if (event.getSlot() == 0) {
+                    DustsMenu.openDustsMenu(player);
+                }
+            }
+        }
+    }
+}

