package org.ThefryGuy.techFactory.gui;

import org.ThefryGuy.techFactory.recipes.dusts.*;
import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.ThefryGuy.techFactory.gui.menubuttons.BackButton;

public class RecipeMenu {

    /**
     * Maps ore materials to dust types
     */
    private static Material getOreMaterial(String dustId) {
        return switch (dustId) {
            case "iron_dust" -> Material.IRON_ORE;
            case "gold_dust" -> Material.GOLD_ORE;
            case "copper_dust" -> Material.COPPER_ORE;
            case "tin_dust" -> Material.STONE; // Placeholder - adjust as needed
            case "silver_dust" -> Material.STONE; // Placeholder - adjust as needed
            case "aluminum_dust" -> Material.STONE; // Placeholder - adjust as needed
            case "lead_dust" -> Material.STONE; // Placeholder - adjust as needed
            case "zinc_dust" -> Material.STONE; // Placeholder - adjust as needed
            case "magnesium_dust" -> Material.STONE; // Placeholder - adjust as needed
            default -> Material.STONE;
        };
    }

    public static void openRecipeMenu(Player player, DustType dust) {
        // Create an 18-slot chest inventory (2 rows) for better clarity
        Inventory gui = Bukkit.createInventory(null, 18, ChatColor.LIGHT_PURPLE + dust.getDisplayName() + " Recipe");

        // ===== ROW 1: LABELS & SECTIONS =====
        // Slot 0: Back button
        gui.setItem(0, BackButton.getItem());

        // Slot 1: INPUT label
        gui.setItem(1, createLabel(ChatColor.AQUA + "INPUT", "Material to grind"));

        // Slot 2: Glass separator
        gui.setItem(2, createGlassSeparator());

        // Slot 3: MACHINE label
        gui.setItem(3, createLabel(ChatColor.DARK_GRAY + "MACHINE", "Processing device"));

        // Slot 4: Glass separator
        gui.setItem(4, createGlassSeparator());

        // Slot 5: OUTPUT label
        gui.setItem(5, createLabel(ChatColor.LIGHT_PURPLE + "OUTPUT", "Final product"));

        // Slot 6-8: Empty glass
        gui.setItem(6, createGlassSeparator());
        gui.setItem(7, createGlassSeparator());
        gui.setItem(8, createGlassSeparator());

        // ===== ROW 2: RECIPE FLOW =====
        // Slot 9: Empty
        gui.setItem(9, createGlassSeparator());

        // Slot 10: Input material (ore to grind)
        gui.setItem(10, createInputItem(dust));

        // Slot 11: Glass separator
        gui.setItem(11, createGlassSeparator());

        // Slot 12: Grinding stone (machine)
        gui.setItem(12, createGrinder());

        // Slot 13: Arrow (flow indicator)
        gui.setItem(13, createArrow());

        // Slot 14: Output dust
        gui.setItem(14, createOutputItem(dust));

        // Slot 15-17: Glass separators
        gui.setItem(15, createGlassSeparator());
        gui.setItem(16, createGlassSeparator());
        gui.setItem(17, createGlassSeparator());

        // Open the inventory for the player
        player.openInventory(gui);
    }

    /**
     * Creates a label item for section headers
     */
    private static ItemStack createLabel(String name, String description) {
        ItemStack item = new ItemStack(Material.NETHER_STAR);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(name);
            meta.setLore(java.util.List.of(
                    ChatColor.GRAY + description
            ));
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Creates a glass pane separator for visual clarity
     */
    private static ItemStack createGlassSeparator() {
        ItemStack item = new ItemStack(Material.LIGHT_GRAY_STAINED_GLASS_PANE);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(" ");
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Creates the input ore item
     */
    private static ItemStack createInputItem(DustType dust) {
        Material oreMaterial = getOreMaterial(dust.getId());
        ItemStack item = new ItemStack(oreMaterial);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(ChatColor.AQUA + "Input Ore");
            meta.setLore(java.util.List.of(
                    ChatColor.GRAY + "Raw material needed",
                    ChatColor.GRAY + "Grind to create " + dust.getDisplayName()
            ));
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Creates the grinding stone (machine) item
     */
    private static ItemStack createGrinder() {
        ItemStack item = new ItemStack(Material.DARK_OAK_WOOD);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(ChatColor.DARK_GRAY + "Grinding Stone");
            meta.setLore(java.util.List.of(
                    ChatColor.GRAY + "Industrial machine",
                    ChatColor.GRAY + "Grinds ore into powder"
            ));
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Creates an arrow flow indicator item
     */
    private static ItemStack createArrow() {
        ItemStack item = new ItemStack(Material.ARROW);
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(ChatColor.YELLOW + "Processing");
            meta.setLore(java.util.List.of(
                    ChatColor.GRAY + "Ore is processed",
                    ChatColor.GRAY + "and converted"
            ));
            item.setItemMeta(meta);
        }

        return item;
    }

    /**
     * Creates the output dust item with enhanced display
     */
    private static ItemStack createOutputItem(DustType dust) {
        ItemStack item = dust.getItemStack();
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setLore(java.util.List.of(
                    ChatColor.GRAY + "Final product obtained",
                    ChatColor.GRAY + "from grinding ore",
                    ChatColor.LIGHT_PURPLE + "Ready for crafting!"
            ));
            item.setItemMeta(meta);
        }

        return item;
    }
}

