    public String getId() {
        return "sulfate";
    }

    @Override
    public String getDisplayName() {
        return "Sulfate";
    }

    @Override
    public ChatColor getColor() {
        return ChatColor.YELLOW;
    }

    @Override
    public Material getMaterial() {
        return Material.SULFUR;
    }

    @Override
    public List<String> getLore() {
        return List.of(
                ChatColor.GRAY + "A chemical compound",
                ChatColor.GRAY + "Extracted from Netherrack",
                ChatColor.GRAY + "Used in electronic components"
        );
    }

    @Override
    public ItemStack[] getRecipe() {
        // 16x Netherrack → 1x Sulfate (in Ore Crusher)
        ItemStack netherrack = new ItemStack(Material.NETHERRACK);
        return new ItemStack[] {
            netherrack.clone(), netherrack.clone(), netherrack.clone(),
            netherrack.clone(), netherrack.clone(), netherrack.clone(),
            netherrack.clone(), netherrack.clone(), netherrack.clone()
        };
    }

    @Override
    public String getMachineType() {
        return "Ore Crusher";
    }
}

