+package org.ThefryGuy.techFactory.listeners;
+
+import org.bukkit.ChatColor;
+import org.bukkit.Material;
+import org.bukkit.block.Block;
+import org.bukkit.entity.Player;
+import org.bukkit.event.EventHandler;
+import org.bukkit.event.Listener;
+import org.bukkit.event.block.Action;
+import org.bukkit.event.player.PlayerInteractEvent;
+import org.bukkit.inventory.ItemStack;
+import org.ThefryGuy.techFactory.recipes.RecipeItem;
+import org.ThefryGuy.techFactory.recipes.dusts.SiftedOreDust;
+
+import java.util.Random;
+
+/**
+ * Listener for tool interactions.
+ * 
+ * Handles:
+ * - Gold Pan right-click on gravel
+ */
+public class ToolListener implements Listener {
+
+    private static final Random random = new Random();
+
+    /**
+     * Handle player right-clicking with tools.
+     */
+    @EventHandler
+    public void onPlayerInteract(PlayerInteractEvent event) {
+        // Only care about right-clicks on blocks
+        if (event.getAction() != Action.RIGHT_CLICK_BLOCK) {
+            return;
+        }
+
+        Player player = event.getPlayer();
+        ItemStack itemInHand = player.getInventory().getItemInMainHand();
+
+        // Check if player is holding a Gold Pan
+        if (itemInHand != null && RecipeItem.isValidItem(itemInHand, "gold_pan")) {
+            Block clickedBlock = event.getClickedBlock();
+            if (clickedBlock != null && clickedBlock.getType() == Material.GRAVEL) {
+                event.setCancelled(true);
+                processGoldPan(player, clickedBlock);
+                return;
+            }
+        }
+    }
+
+    /**
+     * Process the Gold Pan on gravel.
+     * Random outputs from the table.
+     * 
+     * @param player The player using the pan
+     * @param gravelBlock The gravel block being panned
+     */
+    private void processGoldPan(Player player, Block gravelBlock) {
+        // Randomly choose what the player gets
+        int roll = random.nextInt(100);
+
+        ItemStack result;
+
+        if (roll < 40) {
+            // 40% chance: Iron Nugget
+            result = new ItemStack(Material.IRON_NUGGET);
+            player.sendMessage(ChatColor.GOLD + "You panned out an " + ChatColor.WHITE + "Iron Nugget!");
+        } else if (roll < 75) {
+            // 35% chance: Sifted Ore Dust
+            SiftedOreDust dust = new SiftedOreDust();
+            result = dust.getItemStack();
+            player.sendMessage(ChatColor.GOLD + "You panned out " + ChatColor.WHITE + "Sifted Ore Dust!");
+        } else if (roll < 90) {
+            // 15% chance: Clay Ball
+            result = new ItemStack(Material.CLAY_BALL);
+            player.sendMessage(ChatColor.GOLD + "You panned out a " + ChatColor.WHITE + "Clay Ball!");
+        } else {
+            // 10% chance: Flint
+            result = new ItemStack(Material.FLINT);
+            player.sendMessage(ChatColor.GOLD + "You panned out " + ChatColor.WHITE + "Flint!");
+        }
+
+        // Drop the item at the gravel block location
+        gravelBlock.getWorld().dropItem(gravelBlock.getLocation().add(0.5, 1, 0.5), result);
+
+        // Remove one gravel from the block (if it has multiple)
+        if (gravelBlock.getType() == Material.GRAVEL) {
+            // In Minecraft, gravel blocks are single blocks, so we just remove it
+            // If the block is part of a larger gravel area, we remove it
+            gravelBlock.setType(Material.AIR);
+        }
+
+        // Add slight cooldown message
+        player.sendMessage(ChatColor.GRAY + "Ready to pan again!");
+    }
+}

