package org.ThefryGuy.techFactory.workstations.multiblocks;

import org.bukkit.Bukkit;
import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.block.Block;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.InventoryHolder;
import org.bukkit.inventory.ItemStack;
import org.ThefryGuy.techFactory.recipes.dusts.SiftedOreDust;

import java.util.HashMap;
import java.util.UUID;

/**
 * Automated Panning Machine - Handles the actual functionality
 * 
 * Structure:
 * [Trapdoor]  ← Top
 * [Cauldron]  ← Bottom (player clicks here, holds items)
 * 
 * Process:
 * Gravel → Sifted Ore Dust
 * 
 * The recipe data is in recipes/workstations/multiblocks/AutomatedPanning.java
 */
public class AutomatedPanningMachineMachine {

    // Track which player opened which cauldron
    private static final HashMap<UUID, Location> playerCauldronMap = new HashMap<>();

    /**
     * Check if a valid Automated Panning Machine multiblock exists at this location.
     * 
     * Structure:
     * [Trapdoor]  ← Top
     * [Cauldron]  ← Bottom (player clicks here)
     * 
     * @param cauldron The cauldron block (bottom)
     * @return true if valid multiblock structure
     */
    public static boolean isValidStructure(Block cauldron) {
        // Check if bottom is a cauldron
        if (cauldron.getType() != Material.CAULDRON) {
            return false;
        }

        // Check if block above is a trapdoor
        Block above = cauldron.getRelative(0, 1, 0);
        return isTrapdoor(above.getType());
    }

    /**
     * Check if a material is a trapdoor type
     */
    private static boolean isTrapdoor(Material material) {
        return material == Material.OAK_TRAPDOOR
            || material == Material.SPRUCE_TRAPDOOR
            || material == Material.BIRCH_TRAPDOOR
            || material == Material.JUNGLE_TRAPDOOR
            || material == Material.ACACIA_TRAPDOOR
            || material == Material.DARK_OAK_TRAPDOOR
            || material == Material.CRIMSON_TRAPDOOR
            || material == Material.WARPED_TRAPDOOR
            || material == Material.IRON_TRAPDOOR;
    }

    /**
     * Open the Automated Panning Machine GUI for the player.
     * This creates a custom inventory where they can place gravel.
     * 
     * @param player The player who clicked
     * @param cauldron The cauldron block
     */
    public static void openInventory(Player player, Block cauldron) {
        // Create a custom inventory (9 slots like a dispenser)
        Inventory inv = Bukkit.createInventory(new AutomatedPanningInventoryHolder(cauldron.getLocation()), 9, ChatColor.GOLD + "Automated Panning Machine");

        // Store the cauldron location mapped to player
        playerCauldronMap.put(player.getUniqueId(), cauldron.getLocation());

        player.openInventory(inv);
        player.sendMessage(ChatColor.GREEN + "Automated Panning Machine opened!");
        player.sendMessage(ChatColor.GRAY + "Place gravel and close to process.");
    }

    /**
     * Get the stored cauldron location for a player
     */
    public static Location getCauldronLocation(Player player) {
        return playerCauldronMap.remove(player.getUniqueId());
    }

    /**
     * Process panning recipes when the player closes the inventory.
     * Output goes into an adjacent chest or drops on the ground.
     *
     * Process: Gravel → Sifted Ore Dust
     *
     * @param inv The inventory
     * @param player The player who closed it
     * @param cauldronLocation The location of the cauldron
     */
    public static void processRecipe(Inventory inv, Player player, Location cauldronLocation) {
        // Check for Gravel → Sifted Ore Dust
        int gravelCount = 0;
        for (int i = 0; i < inv.getSize(); i++) {
            ItemStack item = inv.getItem(i);
            if (item != null && item.getType() == Material.GRAVEL) {
                gravelCount += item.getAmount();
                inv.setItem(i, null); // Remove gravel
            }
        }

        if (gravelCount > 0) {
            SiftedOreDust siftedOreDust = new SiftedOreDust();
            ItemStack output = siftedOreDust.getItemStack();
            output.setAmount(gravelCount);

            Block cauldron = cauldronLocation.getBlock();

            // Try to output to adjacent chest first
            if (!outputToChest(cauldron, output)) {
                // No chest found, drop at cauldron location
                cauldronLocation.getWorld().dropItem(cauldronLocation.add(0.5, 1, 0.5), output);
            }

            player.sendMessage(ChatColor.GREEN + "Panned " + gravelCount + " gravel into Sifted Ore Dust!");
        }
    }

    /**
     * Try to output an item to an adjacent chest.
     * Checks all 6 directions (N, S, E, W, Up, Down) for a chest.
     * 
     * @param cauldronBlock The cauldron block
     * @param output The item to output
     * @return true if successfully output to a chest, false otherwise
     */
    private static boolean outputToChest(Block cauldronBlock, ItemStack output) {
        // Check all 6 directions for a chest
        Block[] neighbors = {
            cauldronBlock.getRelative(1, 0, 0),   // East
            cauldronBlock.getRelative(-1, 0, 0),  // West
            cauldronBlock.getRelative(0, 0, 1),   // South
            cauldronBlock.getRelative(0, 0, -1),  // North
            cauldronBlock.getRelative(0, 1, 0),   // Up
            cauldronBlock.getRelative(0, -1, 0)   // Down
        };

        for (Block neighbor : neighbors) {
            if (neighbor.getType() == Material.CHEST) {
                org.bukkit.block.Chest chest = (org.bukkit.block.Chest) neighbor.getState();
                Inventory chestInv = chest.getInventory();

                // Try to add the item to the chest
                HashMap<Integer, ItemStack> leftover = chestInv.addItem(output);
                return leftover.isEmpty(); // Return true if all items were added
            }
        }

        return false; // No chest found
    }

    /**
     * Break the multiblock and drop items.
     * Called when any part of the multiblock is broken.
     * 
     * @param location The location where it was broken
     * @param player The player who broke it (can be null)
     */
    public static void breakMultiblock(Location location, Player player) {
        // TODO: Drop the Automated Panning Machine controller item
        // For now, just send a message
        if (player != null) {
            player.sendMessage(ChatColor.RED + "Automated Panning Machine destroyed!");
        }
    }

    /**
     * Custom InventoryHolder for Automated Panning Machine inventory.
     * Stores the cauldron location for reference.
     */
    public static class AutomatedPanningInventoryHolder implements InventoryHolder {
        private final Location cauldronLocation;

        public AutomatedPanningInventoryHolder(Location cauldronLocation) {
            this.cauldronLocation = cauldronLocation;
        }

        @Override
        public Inventory getInventory() {
            return null; // Not used
        }

        public Location getCauldronLocation() {
            return cauldronLocation;
        }
    }
}

