package org.ThefryGuy.techFactory.gui;

import org.ThefryGuy.techFactory.recipes.RecipeItem;
import org.bukkit.ChatColor;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.ThefryGuy.techFactory.gui.framework.Menu;
import org.ThefryGuy.techFactory.gui.menubuttons.BackButton;
import org.ThefryGuy.techFactory.gui.menubuttons.HomeButton;

/**
 * Recipe menu - displays how to craft a specific item (dust, ingot, etc.)
 * Slimefun-style layout: Machine on left, 3x3 grid in middle, output on right
 * Uses 63-slot inventory (7 rows √ó 9 columns) for organized visual layout:
 * - Row 0: Title
 * - Row 1: Separator
 * - Row 2: Section headers (MACHINE | RECIPE | OUTPUT)
 * - Row 3: Separator
 * - Rows 4-6: Main content (Machine | 3x3 Grid | Arrows | Output)
 */
public class RecipeMenu extends Menu {

    private final RecipeItem recipeItem;

    /**
     * Constructor - takes a recipe item to display
     */
    private RecipeMenu(RecipeItem recipeItem) {
        super(ChatColor.LIGHT_PURPLE + recipeItem.getDisplayName() + " Recipe", 63);
        this.recipeItem = recipeItem;
    }

    /**
     * Factory method to open recipe menu for a player
     */
    public static void openRecipeMenu(Player player, RecipeItem recipeItem) {
        RecipeMenu menu = new RecipeMenu(recipeItem);
        menu.openForPlayer(player);
    }

    @Override
    protected void buildMenu() {
        // Clear any previous items
        inventory.clear();

        // Fill entire inventory with glass separators first
        for (int i = 0; i < 63; i++) {
            setItem(i, createGlassSeparator());
        }

        // ===== ROW 0 (0-8): TITLE =====
        fillRowWithGlass(0, 9);
        setItem(4, createTitle());

        // ===== ROW 1 (9-17): SEPARATOR =====
        fillRowWithGlass(9, 18);

        // ===== ROW 2 (18-26): SECTION HEADERS =====
        setItem(18, createSectionHeader(ChatColor.DARK_GRAY + "üì¶ MACHINE", "Machine Required"));
        setItem(19, createGlassSeparator());
        setItem(20, createSectionHeader(ChatColor.YELLOW + "üß± RECIPE", "Crafting Grid"));
        setItem(21, createGlassSeparator());
        setItem(22, createGlassSeparator());
        setItem(23, createGlassSeparator());
        setItem(24, createSectionHeader(ChatColor.LIGHT_PURPLE + "üéÅ OUTPUT", "Result Item"));
        setItem(25, createGlassSeparator());
        setItem(26, createGlassSeparator());

        // ===== ROW 3 (27-35): SEPARATOR =====
        fillRowWithGlass(27, 36);

        // ===== ROWS 4-6 (36-54): MAIN CONTENT LAYOUT =====
        // Column layout: [Machine] [|] [Grid 3x3] [|] [‚Üí] [Output] [|] [empty]

        // Row 4 - Grid Row 1 & Machine top
        setItem(36, createMachine());      // Column 0: Machine
        setItem(37, createGlassSeparator()); // Column 1: Separator
        setItem(38, createGridItem(0));    // Column 2: Grid[0]
        setItem(39, createGridItem(1));    // Column 3: Grid[1]
        setItem(40, createGridItem(2));    // Column 4: Grid[2]
        setItem(41, createGlassSeparator()); // Column 5: Separator
        setItem(42, createArrow());        // Column 6: Arrow
        setItem(43, createOutputItem());   // Column 7: Output
        setItem(44, createGlassSeparator()); // Column 8: Separator

        // Row 5 - Grid Row 2 & Machine middle
        setItem(45, createGlassSeparator()); // Column 0: Separator
        setItem(46, createGlassSeparator()); // Column 1: Separator
        setItem(47, createGridItem(3));    // Column 2: Grid[3]
        setItem(48, createGridItem(4));    // Column 3: Grid[4] (center input)
        setItem(49, createGridItem(5));    // Column 4: Grid[5]
        setItem(50, createGlassSeparator()); // Column 5: Separator
        setItem(51, createArrow());        // Column 6: Arrow
        setItem(52, createGlassSeparator()); // Column 7: Empty
        setItem(53, createGlassSeparator()); // Column 8: Separator

        // Row 6 - Grid Row 3 & Machine bottom
        setItem(54, createGlassSeparator()); // Column 0: Separator
        setItem(55, createGlassSeparator()); // Column 1: Separator
