public interface ItemHandler {

    /**
     * Validates this handler is compatible with the item.
     * Return empty Optional if valid.
     */
    @Nonnull
    default Optional<Exception> validate(@Nonnull RecipeItem item) {
        return Optional.empty();
    }

    /**
     * Unique identifier for this handler type.
     * Used to prevent duplicate handlers.
     */
