+        List<String> itemList = new java.util.ArrayList<>();
+        for (String item : items) {
+            // Add ":1" if no quantity specified
+            if (!item.contains(":")) {
+                itemList.add(item + ":1");
+            } else {
+                itemList.add(item);
+            }
+        }
+        java.util.Collections.sort(itemList);
+        String recipeKey = String.join(",", itemList);
