package org.ThefryGuy.techFactory.listeners;

import org.bukkit.ChatColor;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.block.Block;
import org.bukkit.block.Dispenser;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.block.Action;
import org.bukkit.event.inventory.InventoryCloseEvent;
import org.bukkit.event.player.PlayerInteractEvent;
import org.bukkit.inventory.InventoryHolder;
import org.ThefryGuy.techFactory.TechFactory;
import org.ThefryGuy.techFactory.workstations.multiblocks.*;

import org.bukkit.plugin.java.JavaPlugin;

/**
 * Listener for multiblock machine interactions.
 *
 * Handles:
 * - Right-clicking multiblocks to open them
 * - Closing multiblock inventories to process recipes
 * - Breaking multiblocks
 */
public class MultiblockListener implements Listener {

    private final TechFactory plugin;

    public MultiblockListener(TechFactory plugin) {
        this.plugin = plugin;
    }

    /**
     * Handle player right-clicking blocks.
     * Check if they're clicking a multiblock machine.
     */
    @EventHandler
    public void onPlayerInteract(PlayerInteractEvent event) {
        // Only care about right-clicks on blocks
        if (event.getAction() != Action.RIGHT_CLICK_BLOCK) {
            return;
        }

        Block clicked = event.getClickedBlock();
        if (clicked == null) {
            return;
        }

        Player player = event.getPlayer();

        // Check if it's a Basic Workbench (Crafting Table on Dispenser)
        if (clicked.getType() == Material.CRAFTING_TABLE) {
            if (BasicWorkbenchMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal crafting table opening
                BasicWorkbenchMachine.openWorkbench(player, clicked);
                return;
            }
        }

        // Check if it's a Dispenser (could be Sifter, Pressure Chamber, or Ore Washer)
        if (clicked.getType() == Material.DISPENSER) {
            // Check Pressure Chamber first (dispenser facing down)
            if (PressureChamberMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal dispenser opening
                PressureChamberMachine.openInventory(clicked, player);
                return;
            }

            // Check Ore Washer (dispenser on fence on cauldron)
            if (OreWasherMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal dispenser opening
                OreWasherMachine.openInventory(player, clicked);
                return;
            }

            // Legacy: Check Auto Panning Machine (dispenser on fence on cauldron)
            // This is kept for backward compatibility
            if (AutomatedPanningMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal dispenser opening
                AutomatedPanningMachine.openInventory(clicked, player);
                return;
            }
        }

        // Check if it's a Smelter (Blast Furnace with Iron Bars, Bricks, and Campfire)
        if (clicked.getType() == Material.BLAST_FURNACE) {
            if (SmelterMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal blast furnace opening
                SmelterMachine.openInventory(clicked, player);
                return;
            }
        }

        // Check if it's a Compressor (Fence on Dispenser with Pistons)
        if (clicked.getType() == Material.OAK_FENCE || clicked.getType() == Material.SPRUCE_FENCE
            || clicked.getType() == Material.BIRCH_FENCE || clicked.getType() == Material.JUNGLE_FENCE
            || clicked.getType() == Material.ACACIA_FENCE || clicked.getType() == Material.DARK_OAK_FENCE
            || clicked.getType() == Material.CRIMSON_FENCE || clicked.getType() == Material.WARPED_FENCE) {
            if (CompressorMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal fence interaction
                CompressorMachine.openInventory(clicked, player);
                return;
            }
        }

        // Check if it's an Automated Panning Machine (Cauldron with Trapdoor)
        if (clicked.getType() == Material.CAULDRON) {
            if (AutomatedPanningMachine.isValidStructure(clicked)) {
                event.setCancelled(true); // Prevent normal cauldron interaction
                AutomatedPanningMachine.openInventory(clicked, player);
                return;
            }
        }

        // Add more multiblock checks here as you create them
    }

    /**
     * Handle player closing inventories.
     * If they close a multiblock inventory, process the recipe.
     */
    @EventHandler
    public void onInventoryClose(InventoryCloseEvent event) {
        InventoryHolder holder = event.getInventory().getHolder();
        Player player = (Player) event.getPlayer();
        String title = event.getView().getTitle();

        // Check if it's a dispenser (both Basic Workbench and Sifter use dispensers)
        if (holder instanceof Dispenser) {
            Dispenser dispenser = (Dispenser) holder;
            Block dispenserBlock = dispenser.getBlock();

            // Check if this dispenser is part of a Basic Workbench
            Block above = dispenserBlock.getRelative(0, 1, 0);
            if (above.getType() == Material.CRAFTING_TABLE) {
                if (BasicWorkbenchMachine.isValidStructure(above)) {
                    BasicWorkbenchMachine.processRecipe(dispenser, player);
                    return;
                }
            }

            // Check if this dispenser is part of a Pressure Chamber
            if (PressureChamberMachine.isValidStructure(dispenserBlock)) {
                PressureChamberMachine.processRecipe(dispenser, player);
                return;
            }

            // Check if this dispenser is part of an Ore Washer
            if (OreWasherMachine.isValidStructure(dispenserBlock)) {
                OreWasherMachine.processRecipe(dispenser, player);
                return;
            }

            // Check if this dispenser is part of an Auto Panning Machine (legacy)
            if (AutomatedPanningMachine.isValidStructure(dispenserBlock)) {
                AutomatedPanningMachine.processRecipe(dispenser, player);
                return;
            }

            // Check if this dispenser is part of a Compressor
            Block aboveCompressorFence = dispenserBlock.getRelative(0, 1, 0);
            if (aboveCompressorFence.getType() == Material.OAK_FENCE || aboveCompressorFence.getType() == Material.SPRUCE_FENCE
                || aboveCompressorFence.getType() == Material.BIRCH_FENCE || aboveCompressorFence.getType() == Material.JUNGLE_FENCE
                || aboveCompressorFence.getType() == Material.ACACIA_FENCE || aboveCompressorFence.getType() == Material.DARK_OAK_FENCE
                || aboveCompressorFence.getType() == Material.CRIMSON_FENCE || aboveCompressorFence.getType() == Material.WARPED_FENCE) {
                if (CompressorMachine.isValidStructure(aboveCompressorFence)) {
                    CompressorMachine.processRecipe(dispenser, player);
                    return;
                }
            }
        }

        // Check if it's an Automated Panning Machine inventory (custom inventory)
        if (title.equals(ChatColor.GOLD + "Automated Panning Machine")) {
            if (holder instanceof AutomatedPanningMachine.AutomatedPanningInventoryHolder) {
                AutomatedPanningMachine.AutomatedPanningInventoryHolder panHolder = 
                    (AutomatedPanningMachine.AutomatedPanningInventoryHolder) holder;
                Location cauldronLocation = panHolder.getCauldronLocation();
                if (cauldronLocation != null) {
                    AutomatedPanningMachine.processRecipe(event.getInventory(), player, cauldronLocation);
                }
            }
            return;
        }

        // Check if it's a Smelter inventory (custom inventory, not dispenser)
        if (title.equals(ChatColor.GOLD + "Smelter")) {
            SmelterMachine.onClose(player, event.getInventory(), plugin);
        }

        // Add more multiblock inventory checks here
    }

    // TODO: Add block break listener to handle multiblock destruction
    // @EventHandler
    // public void onBlockBreak(BlockBreakEvent event) {
    //     Block broken = event.getBlock();
    //     
    //     // Check if breaking part of a multiblock
    //     // If so, destroy the multiblock and drop items
    // }
}

