public class AluminumDust implements RecipeItem {

    @Override
    public ItemStack getItemStack() {
        ItemStack item = new ItemStack(getMaterial());
        ItemMeta meta = item.getItemMeta();

        if (meta != null) {
            meta.setDisplayName(getColor() + getDisplayName());
            meta.setLore(getLore());
            item.setItemMeta(meta);
        }

        return item;
    }

    @Override
    public String getId() {
        return "aluminum_dust";
    }

    @Override
    public String getDisplayName() {
        return "Aluminum Dust";
    }

    @Override
    public ChatColor getColor() {
        return ChatColor.WHITE;
    }

    @Override
    public Material getMaterial() {
        return Material.GUNPOWDER;
    }

    @Override
    public List<String> getLore() {
        return List.of(
                ChatColor.GRAY + "Lightweight metal powder",
                ChatColor.GRAY + "Used in aerospace materials"
        );
    }

    @Override
    public String getRecipeType() {
        return "smelting";
    }

    @Override
    public String getInputName() {
        return "Input Sifted Ore Dust";
    }

    @Override
    public String getMachineType() {
        return "Smelter";
    }

    @Override
    public Material getInputMaterial() {
        return Material.GUNPOWDER;
    }

    @Override
    public String getRecipe() {
        return "Recipe: Sifted Ore Dust + Smelter";
    }
}

