+import org.bukkit.inventory.ItemStack;
+import org.bukkit.inventory.meta.ItemMeta;
+
+/**
+ * Utility builder for creating ItemStacks from RecipeItem implementations.
+ * Centralizes the common logic for building item stacks with display names and lore.
+ */
+public class RecipeItemBuilder {
+
+    /**
+     * Builds an ItemStack from a RecipeItem with display name and lore.
+     *
+     * @param item the RecipeItem to build from
+     * @return a configured ItemStack
+     */
+    public static ItemStack buildItemStack(RecipeItem item) {
+        ItemStack stack = new ItemStack(item.getMaterial());
+        ItemMeta meta = stack.getItemMeta();
+
+        if (meta != null) {
+            meta.setDisplayName(item.getColor() + item.getDisplayName());
+            meta.setLore(item.getLore());
+            stack.setItemMeta(meta);
+        }
+
+        return stack;
+    }
+}

